(1, 7, 'keywords', ' Cận cảnh sự xuống cấp của chợ An Đông ở Sài Gòn, Can canh su xuong cap cua cho An Dong o Sai Gon, Cận, cảnh, sự, xuống, cấp, của, chợ, An, Đông, ở, Sài, Gòn, Can, canh, su, xuong, cap, cua, cho, An, Dong, o, Sai, Gon,'),
(2, 8, 'keywords', ' Hà Văn Thắm: Tài sản 1.000 tỷ của bị cáo giờ chỉ còn 100 tỷ, Ha Van Tham: Tai san 1.000 ty cua bi cao gio chi con 100 ty, Hà, Văn, Thắm:, Tài, sản, 1.000, tỷ, của, bị, cáo, giờ, chỉ, còn, 100, tỷ, Ha, Van, Tham:, Tai, san, 1.000, ty, cua, bi, cao, gio, chi, con, 100, ty,'),
(3, 9, 'keywords', ' Áp thấp nhiệt đới hướng vào Quảng Ninh - Hải Phòng, Ap thap nhiet doi huong vao Quang Ninh - Hai Phong, Áp, thấp, nhiệt, đới, hướng, vào, Quảng, Ninh, -, Hải, Phòng, Ap, thap, nhiet, doi, huong, vao, Quang, Ninh, -, Hai, Phong,'),
